  <script type="application/json" data-target="react-app.embeddedData">{"payload":{"allShortcutsEnabled":true,"fileTree":{"":{"items":[{"name":"GMCP Mapper.xml","path":"GMCP Mapper.xml","contentType":"file"},{"name":"mapper.lua","path":"mapper.lua","contentType":"file"}],"totalCount":2}},"fileTreeProcessingTime":2.100517,"foldersToFetch":[],"incompleteFileTree":false,"repo":{"id":1047677845,"defaultBranch":"main","name":"Aesir-Reborn-Mushclient","ownerLogin":"Galikin2002","currentUserCanPush":true,"isFork":false,"isEmpty":false,"createdAt":"2025-08-31T10:54:13.000+10:00","ownerAvatar":"https://avatars.githubusercontent.com/u/229539196?v=4","public":false,"private":true,"isOrgOwned":false},"codeLineWrapEnabled":false,"symbolsExpanded":false,"treeExpanded":true,"refInfo":{"name":"main","listCacheKey":"v0:1756601676.0","canEdit":true,"refType":"branch","currentOid":"836c6ccd65185109a796bbef6e84def64b5e2e92"},"path":"mapper.lua","currentUser":{"id":229539196,"login":"Galikin2002","userEmail":"worldpedo.doug@gmail.com"},"blob":{"rawLines":["-- mapper.lua\r","\r","--[[\r","\r","Author: Nick Gammon\r","Date:   11th March 2010\r","Amended: 15th August 2010\r","Amended: 2nd October 2010\r","Amended: 18th October 2010 to added find callback\r","Amended: 16th November 2010 to add symbolic constants (miniwin.xxxx)\r","Amended: 18th November 2010 to add more timing and count of times called\r","         Also added zooming with the mouse wheel.\r","Amended: 26th November 2010 to check timers are enabled when speedwalking.\r","Amended: 11th November 2014 to allow for detecting mouse-overs of rooms\r","\r","Generic MUD mapper.\r","\r","Exposed functions:\r","\r","init (t)            -- call once, supply:\r","                          t.config      -- ie. colours, sizes\r","                          t.get_room    -- info about room (uid)\r","                          t.show_help   -- function that displays some help\r","                          t.room_click  -- function that handles RH click on room (uid, flags)\r","                          t.room_mouseover -- function that handles mouse-over a room (uid, flags)\r","                          t.room_cancelmouseover -- function that handles cancelled mouse-over of a room (uid, flags)\r","                          t.timing      -- true to show timing\r","                          t.show_completed  -- true to show \"Speedwalk completed.\"\r","                          t.show_other_areas -- true to show non-current areas\r","                          t.show_up_down    -- follow up/down exits\r","                          t.show_area_exits  -- true to draw a circle around rooms leading to other areas\r","                          t.speedwalk_prefix   -- if not nil, speedwalk by prefixing with this\r","\r","zoom_in ()          -- zoom in map view\r","zoom_out ()         -- zoom out map view\r","mapprint (message)  -- like print, but uses mapper colour\r","maperror (message)  -- like print, but prints in red\r","hide ()             -- hides map window (eg. if plugin disabled)\r","show ()             -- show map window  (eg. if plugin enabled)\r","save_state ()       -- call to save plugin state (ie. in OnPluginSaveState)\r","draw (uid)          -- draw map - starting at room 'uid'\r","start_speedwalk (path)  -- starts speedwalking. path is a table of directions/uids\r","build_speedwalk (path)  -- builds a client speedwalk string from path\r","cancel_speedwalk ()     -- cancel current speedwalk, if any\r","check_we_can_find ()    -- returns true if doing a find is OK right now\r","find (f, show_uid, count, walk)      -- generic room finder\r","find_paths (uid, f)     -- lower-level room finder (for getting back a path)\r","\r","Exposed variables:\r","\r","win                 -- the window (in case you want to put up menus)\r","VERSION             -- mapper version\r","last_hyperlink_uid  -- room uid of last hyperlink click (destination)\r","last_speedwalk_uid  -- room uid of last speedwalk attempted (destination)\r","\u003cvarious functions\u003e -- functions required to be global by the client (eg. for mouseup)\r","\r","Room info should include:\r","\r","  name          (what to show as room name)\r","  exits         (table keyed by direction, value is exit uid)\r","  area          (area name)\r","  hovermessage  (what to show when you mouse-over the room)\r","  bordercolour  (colour of room border)     - RGB colour\r","  borderpen     (pen style of room border)  - see WindowCircleOp (values 0 to 6)\r","  borderpenwidth(pen width of room border)  - eg. 1 for normal, 2 for current room\r","  fillcolour    (colour to fill room)       - RGB colour, nil for default\r","  fillbrush     (brush to fill room)        - see WindowCircleOp (values 0 to 12)\r","\r","--]]\r","\r","module (..., package.seeall)\r","\r","VERSION = 2.6   -- for querying by plugins\r","\r","require \"movewindow\"\r","require \"copytable\"\r","require \"gauge\"\r","require \"pairsbykeys\"\r","require \"mw\"\r","\r","local FONT_ID     = \"fn\"  -- internal font identifier\r","local FONT_ID_UL  = \"fnu\" -- internal font identifier - underlined\r","\r","-- size of room box\r","local ROOM_SIZE = 10\r","\r","-- how far away to draw rooms from each other\r","local DISTANCE_TO_NEXT_ROOM = 15\r","\r","-- supplied in init\r","local config  -- configuration table\r","local supplied_get_room\r","local room_click\r","local room_mouseover\r","local room_cancelmouseover\r","local timing            -- true to show timing and other info\r","local show_completed    -- true to show \"Speedwalk completed.\"\r","local show_other_areas  -- true to draw other areas\r","local show_area_exits   -- true to show area exits\r","local show_up_down      -- true to show up/down exits\r","\r","-- current room number\r","local current_room\r","\r","-- our copy of rooms info\r","local rooms = {}\r","local last_visited = {}\r","\r","-- other locals\r","local HALF_ROOM, connectors, half_connectors, arrows\r","local plan_to_draw, speedwalks, drawn, drawn_coords\r","local last_drawn, depth, windowinfo, font_height\r","local walk_to_room_name\r","local total_times_drawn = 0\r","local total_time_taken = 0\r","\r","local function build_room_info ()\r","\r","  HALF_ROOM   = ROOM_SIZE / 2\r","  local THIRD_WAY   = DISTANCE_TO_NEXT_ROOM / 3\r","  local DISTANCE_LESS1 = DISTANCE_TO_NEXT_ROOM - 1\r","\r","  -- how to draw a line from this room to the next one (relative to the center of the room)\r","  connectors = {\r","    n =  { x1 = 0,            y1 = - HALF_ROOM, x2 = 0,                             y2 = - HALF_ROOM - DISTANCE_LESS1, at = { 0, -1 } },\r","    s =  { x1 = 0,            y1 =   HALF_ROOM, x2 = 0,                             y2 =   HALF_ROOM + DISTANCE_LESS1, at = { 0,  1 } },\r","    e =  { x1 =   HALF_ROOM,  y1 = 0,           x2 =   HALF_ROOM + DISTANCE_LESS1,  y2 = 0,                            at = {  1,  0 }},\r","    w =  { x1 = - HALF_ROOM,  y1 = 0,           x2 = - HALF_ROOM - DISTANCE_LESS1,  y2 = 0,                            at = { -1,  0 }},\r","\r","    ne = { x1 =   HALF_ROOM,  y1 = - HALF_ROOM, x2 =   HALF_ROOM + DISTANCE_LESS1 , y2 = - HALF_ROOM - DISTANCE_LESS1, at = { 1, -1 } },\r","    se = { x1 =   HALF_ROOM,  y1 =   HALF_ROOM, x2 =   HALF_ROOM + DISTANCE_LESS1 , y2 =   HALF_ROOM + DISTANCE_LESS1, at = { 1,  1 } },\r","    nw = { x1 = - HALF_ROOM,  y1 = - HALF_ROOM, x2 = - HALF_ROOM - DISTANCE_LESS1 , y2 = - HALF_ROOM - DISTANCE_LESS1, at = {-1, -1 } },\r","    sw = { x1 = - HALF_ROOM,  y1 =   HALF_ROOM, x2 = - HALF_ROOM - DISTANCE_LESS1 , y2 =   HALF_ROOM + DISTANCE_LESS1, at = {-1,  1 } },\r","\r","    } -- end connectors\r","\r","  -- how to draw a stub line\r","  half_connectors = {\r","    n =  { x1 = 0,            y1 = - HALF_ROOM, x2 = 0,                        y2 = - HALF_ROOM - THIRD_WAY, at = { 0, -1 } },\r","    s =  { x1 = 0,            y1 =   HALF_ROOM, x2 = 0,                        y2 =   HALF_ROOM + THIRD_WAY, at = { 0,  1 } },\r","    e =  { x1 =   HALF_ROOM,  y1 = 0,           x2 =   HALF_ROOM + THIRD_WAY,  y2 = 0,                       at = {  1,  0 }},\r","    w =  { x1 = - HALF_ROOM,  y1 = 0,           x2 = - HALF_ROOM - THIRD_WAY,  y2 = 0,                       at = { -1,  0 }},\r","\r","    ne = { x1 =   HALF_ROOM,  y1 = - HALF_ROOM, x2 =   HALF_ROOM + THIRD_WAY , y2 = - HALF_ROOM - THIRD_WAY, at = { 1, -1 } },\r","    se = { x1 =   HALF_ROOM,  y1 =   HALF_ROOM, x2 =   HALF_ROOM + THIRD_WAY , y2 =   HALF_ROOM + THIRD_WAY, at = { 1,  1 } },\r","    nw = { x1 = - HALF_ROOM,  y1 = - HALF_ROOM, x2 = - HALF_ROOM - THIRD_WAY , y2 = - HALF_ROOM - THIRD_WAY, at = {-1, -1 } },\r","    sw = { x1 = - HALF_ROOM,  y1 =   HALF_ROOM, x2 = - HALF_ROOM - THIRD_WAY , y2 =   HALF_ROOM + THIRD_WAY, at = {-1,  1 } },\r","\r","    } -- end half_connectors\r","\r","  -- how to draw one-way arrows (relative to the center of the room)\r","  arrows = {\r","     n =  { - 2, - HALF_ROOM - 2,  2, - HALF_ROOM - 2,  0, - HALF_ROOM - 6 },\r","     s =  { - 2,   HALF_ROOM + 2,  2,   HALF_ROOM + 2,  0,   HALF_ROOM + 6  },\r","     e =  {   HALF_ROOM + 2, -2,   HALF_ROOM + 2, 2,   HALF_ROOM + 6, 0 },\r","     w =  { - HALF_ROOM - 2, -2, - HALF_ROOM - 2, 2, - HALF_ROOM - 6, 0 },\r","\r","     ne = {   HALF_ROOM + 3,  - HALF_ROOM,  HALF_ROOM + 3, - HALF_ROOM - 3,  HALF_ROOM, - HALF_ROOM - 3 },\r","     se = {   HALF_ROOM + 3,    HALF_ROOM,  HALF_ROOM + 3,   HALF_ROOM + 3,  HALF_ROOM,   HALF_ROOM + 3 },\r","     nw = { - HALF_ROOM - 3,  - HALF_ROOM,  - HALF_ROOM - 3, - HALF_ROOM - 3,  - HALF_ROOM, - HALF_ROOM - 3 },\r","     sw = { - HALF_ROOM - 3,    HALF_ROOM,  - HALF_ROOM - 3,   HALF_ROOM + 3,  - HALF_ROOM,   HALF_ROOM + 3},\r","\r","    } -- end of arrows\r","\r","end -- build_room_info\r","\r","local default_config = {\r","  -- assorted colours\r","  BACKGROUND_COLOUR       = { name = \"Background\",        colour =  ColourNameToRGB \"lightseagreen\", },\r","  ROOM_COLOUR             = { name = \"Room\",              colour =  ColourNameToRGB \"cyan\", },\r","  EXIT_COLOUR             = { name = \"Exit\",              colour =  ColourNameToRGB \"darkgreen\", },\r","  EXIT_COLOUR_UP_DOWN     = { name = \"Exit up/down\",      colour =  ColourNameToRGB \"darkmagenta\", },\r","  EXIT_COLOUR_IN_OUT      = { name = \"Exit in/out\",       colour =  ColourNameToRGB \"#3775E8\", },\r","  UNKNOWN_ROOM_COLOUR     = { name = \"Unknown room\",      colour =  ColourNameToRGB \"#00CACA\", },\r","  MAPPER_NOTE_COLOUR      = { name = \"Messages\",          colour =  ColourNameToRGB \"lightgreen\" },\r","\r","  ROOM_NAME_TEXT          = { name = \"Room name text\",    colour = ColourNameToRGB \"#BEF3F1\", },\r","  ROOM_NAME_FILL          = { name = \"Room name fill\",    colour = ColourNameToRGB \"#105653\", },\r","  ROOM_NAME_BORDER        = { name = \"Room name box\",     colour = ColourNameToRGB \"black\", },\r","\r","  AREA_NAME_TEXT          = { name = \"Area name text\",    colour = ColourNameToRGB \"#BEF3F1\",},\r","  AREA_NAME_FILL          = { name = \"Area name fill\",    colour = ColourNameToRGB \"#105653\", },\r","  AREA_NAME_BORDER        = { name = \"Area name box\",     colour = ColourNameToRGB \"black\", },\r","\r","  FONT = { name =  get_preferred_font {\"Dina\",  \"Lucida Console\",  \"Fixedsys\", \"Courier\", \"Sylfaen\",} ,\r","           size = 8\r","         } ,\r","\r","  -- size of map window\r","  WINDOW = { width = 400, height = 400 },\r","\r","  -- how far from where we are standing to draw (rooms)\r","  SCAN = { depth = 30 },\r","\r","  -- speedwalk delay\r","  DELAY = { time = 0.3 },\r","\r","  -- how many seconds to show \"recent visit\" lines (default 3 minutes)\r","  LAST_VISIT_TIME = { time = 60 * 3 },\r","\r","  }\r","\r","local expand_direction = {\r","  n = \"north\",\r","  s = \"south\",\r","  e = \"east\",\r","  w = \"west\",\r","  u = \"up\",\r","  d = \"down\",\r","  ne = \"northeast\",\r","  sw = \"southwest\",\r","  nw = \"northwest\",\r","  se = \"southeast\",\r","  ['in'] = \"in\",\r","  out = \"out\",\r","  }  -- end of expand_direction\r","\r","local function get_room (uid)\r","  local room = supplied_get_room (uid)\r","  room = room or { unknown = true }\r","\r","  -- defaults in case they didn't supply them ...\r","  room.name = room.name or string.format (\"Room %s\", uid or \"\u003cnone\u003e\")\r","  room.name = mw.strip_colours (room.name)  -- no colour codes for now\r","  room.exits = room.exits or {}\r","  room.area = room.area or \"\u003cNo area\u003e\"\r","  room.hovermessage = room.hovermessage or \"\u003cUnexplored room\u003e\"\r","  room.bordercolour = room.bordercolour or config.ROOM_COLOUR.colour\r","  room.borderpen = room.borderpen or 0 -- solid\r","  room.borderpenwidth = room.borderpenwidth or 1\r","  room.fillcolour = room.fillcolour or 0x000000\r","  room.fillbrush = room.fillbrush or 1 -- no fill\r","\r","  return room\r","end -- get_room\r","\r","local function check_connected ()\r","  if not IsConnected() then\r","    mapprint (\"You are not connected to\", WorldName())\r","    return false\r","  end -- if not connected\r","  return true\r","end -- check_connected\r","\r","local function make_number_checker (title, min, max, decimals)\r","  return function (s)\r","    local n = tonumber (s)\r","    if not n then\r","      utils.msgbox (title .. \" must be a number\", \"Incorrect input\", \"ok\", \"!\", 1)\r","      return false  -- bad input\r","    end -- if\r","\r","    if n \u003c min or n \u003e max then\r","      utils.msgbox (title .. \" must be in range \" .. min .. \" to \" .. max, \"Incorrect input\", \"ok\", \"!\", 1)\r","      return false  -- bad input\r","    end -- if\r","\r","    if not decimals then\r","      if string.match (s, \"%.\") then\r","        utils.msgbox (title .. \" cannot have decimal places\", \"Incorrect input\", \"ok\", \"!\", 1)\r","        return false  -- bad input\r","      end -- if\r","    end -- no decimals\r","\r","    return true  -- good input\r","  end -- generated function\r","\r","end -- make_number_checker\r","\r","\r","local function get_number_from_user (msg, title, current, min, max, decimals)\r","  local max_length = math.ceil (math.log10 (max) + 1)\r","\r","  -- if decimals allowed, allow room for them\r","  if decimals then\r","    max_length = max_length + 2  -- allow for 0.x\r","  end -- if\r","\r","  -- if can be negative, allow for minus sign\r","  if min \u003c 0 then\r","    max_length = max_length + 1\r","  end -- if can be negative\r","\r","  return tonumber (utils.inputbox (msg, title, current, nil, nil,\r","                      { validate = make_number_checker (title, min, max, decimals),\r","                        prompt_height = 14,\r","                        box_height = 130,\r","                        box_width = 300,\r","                        reply_width = 150,\r","                        max_length = max_length,\r","                      }  -- end extra stuff\r","                   ))\r","end -- get_number_from_user\r","\r","local function draw_configuration ()\r","  local width =  max_text_width (win, FONT_ID, {\"Configuration\", \"Font\", \"Width\", \"Height\", \"Depth\"}, true)\r","  local lines = 6  -- \"Configuration\", font, width, height, depth, delay\r","  local GAP = 5\r","  local suppress_colours = false\r","\r","  for k, v in pairs (config) do\r","    if v.colour then\r","      width = math.max (width, WindowTextWidth (win, FONT_ID, v.name, true))\r","      lines = lines + 1\r","    end -- a colour item\r","  end -- for each config item\r","\r","  if (config.WINDOW.height - 13 - font_height * lines) \u003c 10 then\r","    suppress_colours = true\r","    lines = 6  -- forget all the colours\r","  end -- if\r","\r","  local x = 3\r","  local y = config.WINDOW.height - 13 - font_height * lines\r","  local box_size = font_height - 2\r","  local rh_size = math.max (box_size, max_text_width (win, FONT_ID,\r","    {config.FONT.name .. \" \" .. config.FONT.size,\r","     tostring (config.WINDOW.width),\r","     tostring (config.WINDOW.height),\r","     tostring (config.SCAN.depth)},\r","    true))\r","  local frame_width = GAP + width + GAP + rh_size + GAP  -- gap / text / gap / box / gap\r","\r","  -- fill entire box with grey\r","  WindowRectOp (win, miniwin.rect_fill, x, y, x + frame_width, y + font_height * lines + 10, 0xDCDCDC)\r","  -- frame it\r","  draw_3d_box (win, x, y, frame_width, font_height * lines + 10)\r","\r","  y = y + GAP\r","  x = x + GAP\r","\r","  -- title\r","  WindowText   (win, FONT_ID, \"Configuration\", x, y, 0, 0, 0x808080, true)\r","\r","  -- close box\r","  WindowRectOp (win,\r","                miniwin.rect_frame,\r","                x + frame_width - box_size - GAP * 2,\r","                y + 1,\r","                x + frame_width - GAP * 2,\r","                y + 1 + box_size,\r","                0x808080)\r","  WindowLine (win,\r","              x + frame_width - box_size - GAP * 2 + 3,\r","              y + 4,\r","              x + frame_width - GAP * 2 - 3,\r","              y - 2 + box_size,\r","              0x808080,\r","              miniwin.pen_solid, 1)\r","  WindowLine (win,\r","              x - 4 + frame_width - GAP * 2,\r","              y + 4,\r","              x - 1 + frame_width - box_size - GAP * 2 + 3,\r","              y - 2 + box_size,\r","              0x808080,\r","              miniwin.pen_solid, 1)\r","\r","  -- close configuration hotspot\r","  WindowAddHotspot(win, \"$\u003cclose_configure\u003e\",\r","                   x + frame_width - box_size - GAP * 2,\r","                   y + 1,\r","                   x + frame_width - GAP * 2,\r","                   y + 1 + box_size,   -- rectangle\r","                   \"\", \"\", \"\", \"\", \"mapper.mouseup_close_configure\",  -- mouseup\r","                   \"Click to close\",\r","                   miniwin.cursor_hand, 0)  -- hand cursor\r","\r","  y = y + font_height\r","\r","  if not suppress_colours then\r","\r","    for k, v in pairsByKeys (config) do\r","      if v.colour then\r","        WindowText   (win, FONT_ID, v.name, x, y, 0, 0, 0x000000, true)\r","        WindowRectOp (win,\r","                      miniwin.rect_fill,\r","                      x + width + rh_size / 2,\r","                      y + 1,\r","                      x + width + rh_size / 2 + box_size,\r","                      y + 1 + box_size,\r","                      v.colour)\r","        WindowRectOp (win,\r","                      miniwin.rect_frame,\r","                      x + width + rh_size / 2,\r","                      y + 1,\r","                      x + width + rh_size / 2 + box_size,\r","                      y + 1 + box_size,\r","                      0x000000)\r","\r","        -- colour change hotspot\r","        WindowAddHotspot(win,\r","                         \"$colour:\" .. k,\r","                         x + GAP,\r","                         y + 1,\r","                         x + width + rh_size / 2 + box_size,\r","                         y + 1 + box_size,   -- rectangle\r","                         \"\", \"\", \"\", \"\", \"mapper.mouseup_change_colour\",  -- mouseup\r","                         \"Click to change colour\",\r","                         miniwin.cursor_hand, 0)  -- hand cursor\r","\r","        y = y + font_height\r","      end -- a colour item\r","    end -- for each config item\r","  end -- if\r","\r","  -- depth\r","  WindowText   (win, FONT_ID, \"Depth\", x, y, 0, 0, 0x000000, true)\r","  WindowText   (win, FONT_ID_UL, tostring (config.SCAN.depth), x + width + GAP, y, 0, 0, 0x808080, true)\r","\r","  -- depth hotspot\r","  WindowAddHotspot(win,\r","                   \"$\u003cdepth\u003e\",\r","                   x + GAP,\r","                   y,\r","                   x + frame_width,\r","                   y + font_height,   -- rectangle\r","                   \"\", \"\", \"\", \"\", \"mapper.mouseup_change_depth\",  -- mouseup\r","                   \"Click to change scan depth\",\r","                   miniwin.cursor_hand, 0)  -- hand cursor\r","  y = y + font_height\r","\r","  -- font\r","  WindowText   (win, FONT_ID, \"Font\", x, y, 0, 0, 0x000000, true)\r","  WindowText   (win, FONT_ID_UL, config.FONT.name .. \" \" .. config.FONT.size, x + width + GAP, y, 0, 0, 0x808080, true)\r","\r","  -- colour font hotspot\r","  WindowAddHotspot(win,\r","                   \"$\u003cfont\u003e\",\r","                   x + GAP,\r","                   y,\r","                   x + frame_width,\r","                   y + font_height,   -- rectangle\r","                   \"\", \"\", \"\", \"\", \"mapper.mouseup_change_font\",  -- mouseup\r","                   \"Click to change font\",\r","                   miniwin.cursor_hand, 0)  -- hand cursor\r","  y = y + font_height\r","\r","\r","  -- width\r","  WindowText   (win, FONT_ID, \"Width\", x, y, 0, 0, 0x000000, true)\r","  WindowText   (win, FONT_ID_UL, tostring (config.WINDOW.width), x + width + GAP, y, 0, 0, 0x808080, true)\r","\r","  -- width hotspot\r","  WindowAddHotspot(win,\r","                   \"$\u003cwidth\u003e\",\r","                   x + GAP,\r","                   y,\r","                   x + frame_width,\r","                   y + font_height,   -- rectangle\r","                   \"\", \"\", \"\", \"\", \"mapper.mouseup_change_width\",  -- mouseup\r","                   \"Click to change window width\",\r","                   miniwin.cursor_hand, 0)  -- hand cursor\r","  y = y + font_height\r","\r","  -- height\r","  WindowText   (win, FONT_ID, \"Height\", x, y, 0, 0, 0x000000, true)\r","  WindowText   (win, FONT_ID_UL, tostring (config.WINDOW.height), x + width + GAP, y, 0, 0, 0x808080, true)\r","\r","  -- height hotspot\r","  WindowAddHotspot(win,\r","                   \"$\u003cheight\u003e\",\r","                   x + GAP,\r","                   y,\r","                   x + frame_width,\r","                   y + font_height,   -- rectangle\r","                   \"\", \"\", \"\", \"\", \"mapper.mouseup_change_height\",  -- mouseup\r","                   \"Click to change window height\",\r","                   miniwin.cursor_hand, 0)  -- hand cursor\r","  y = y + font_height\r","\r","  -- delay\r","  WindowText   (win, FONT_ID, \"Walk delay\", x, y, 0, 0, 0x000000, true)\r","  WindowText   (win, FONT_ID_UL, tostring (config.DELAY.time), x + width + GAP, y, 0, 0, 0x808080, true)\r","\r","  -- height hotspot\r","  WindowAddHotspot(win,\r","                   \"$\u003cdelay\u003e\",\r","                   x + GAP,\r","                   y,\r","                   x + frame_width,\r","                   y + font_height,   -- rectangle\r","                   \"\", \"\", \"\", \"\", \"mapper.mouseup_change_delay\",  -- mouseup\r","                   \"Click to change speedwalk delay\",\r","                   miniwin.cursor_hand, 0)  -- hand cursor\r","  y = y + font_height\r","\r","end -- draw_configuration\r","\r","-- for calculating one-way paths\r","local inverse_direction = {\r","  n = \"s\",\r","  s = \"n\",\r","  e = \"w\",\r","  w = \"e\",\r","  u = \"d\",\r","  d = \"u\",\r","  ne = \"sw\",\r","  sw = \"ne\",\r","  nw = \"se\",\r","  se = \"nw\",\r","  ['in'] = \"out\",\r","  out = \"in\",\r","  }  -- end of inverse_direction\r","\r","local function add_another_room (uid, path, x, y)\r","  local path = path or {}\r","  return {uid=uid, path=path, x = x, y = y}\r","end  -- add_another_room\r","\r","local function draw_room (uid, path, x, y)\r","\r","  local coords = string.format (\"%i,%i\", math.floor (x), math.floor (y))\r","\r","  -- need this for the *current* room !!!\r","  drawn_coords [coords] = uid\r","\r","  -- print (\"drawing\", uid, \"at\", coords)\r","\r","  if drawn [uid] then\r","    return\r","  end -- done this one\r","\r","  -- don't draw the same room more than once\r","  drawn [uid] = { coords = coords, path = path }\r","\r","  local room = rooms [uid]\r","\r","  -- not cached - get from caller\r","  if not room then\r","    room = get_room (uid)\r","    rooms [uid] = room\r","  end -- not in cache\r","\r","\r","  local left, top, right, bottom = x - HALF_ROOM, y - HALF_ROOM, x + HALF_ROOM, y + HALF_ROOM\r","\r","  -- forget it if off screen\r","  if x \u003c HALF_ROOM or y \u003c HALF_ROOM or\r","     x \u003e config.WINDOW.width - HALF_ROOM or y \u003e config.WINDOW.height - HALF_ROOM then\r","     return\r","  end -- if\r","\r","  -- exits\r","\r","  local texits = {}\r","\r","  for dir, exit_uid in pairs (room.exits) do\r","    table.insert (texits, dir)\r","    local exit_info = connectors [dir]\r","    local stub_exit_info = half_connectors [dir]\r","    local exit_line_colour = config.EXIT_COLOUR.colour\r","    local arrow = arrows [dir]\r","\r","    -- draw up in the ne/nw position if not already an exit there at this level\r","    if dir == \"u\" then\r","      if not room.exits.nw then\r","        exit_info = connectors.nw\r","        stub_exit_info = half_connectors.nw\r","        arrow = arrows.nw\r","        exit_line_colour = config.EXIT_COLOUR_UP_DOWN.colour\r","      end -- if available\r","    elseif dir == \"in\" then\r","      if not room.exits.ne then\r","        exit_info = connectors.ne\r","        stub_exit_info = half_connectors.ne\r","        arrow = arrows.ne\r","        exit_line_colour = config.EXIT_COLOUR_IN_OUT.colour\r","      end -- if\r","    elseif dir == \"d\" then\r","      if not room.exits.se then\r","        exit_info = connectors.se\r","        stub_exit_info = half_connectors.se\r","        arrow = arrows.se\r","        exit_line_colour = config.EXIT_COLOUR_UP_DOWN.colour\r","      end -- if available\r","    elseif dir == \"out\" then\r","      if not room.exits.sw then\r","        exit_info = connectors.sw\r","        stub_exit_info = half_connectors.sw\r","        arrow = arrows.sw\r","        exit_line_colour = config.EXIT_COLOUR_IN_OUT.colour\r","      end -- if\r","    end -- if down\r","\r","    if exit_info then\r","      local linetype = miniwin.pen_solid -- unbroken\r","      local linewidth = 1 -- not recent\r","\r","      -- try to cache room\r","      if not rooms [exit_uid] then\r","        rooms [exit_uid] = get_room (exit_uid)\r","      end -- if\r","\r","      if rooms [exit_uid].unknown then\r","        linetype = miniwin.pen_dot -- dots\r","      end -- if\r","\r","      local next_x = x + exit_info.at [1] * (ROOM_SIZE + DISTANCE_TO_NEXT_ROOM)\r","      local next_y = y + exit_info.at [2] * (ROOM_SIZE + DISTANCE_TO_NEXT_ROOM)\r","\r","      local next_coords = string.format (\"%i,%i\", math.floor (next_x), math.floor (next_y))\r","\r","      -- remember if a zone exit (first one only)\r","      if show_area_exits and room.area ~= rooms [exit_uid].area then\r","        area_exits [ rooms [exit_uid].area ] = area_exits [ rooms [exit_uid].area ] or {x = x, y = y}\r","      end -- if\r","\r","      -- if another room (not where this one leads to) is already there, only draw \"stub\" lines\r","      if drawn_coords [next_coords] and drawn_coords [next_coords] ~= exit_uid then\r","        exit_info = stub_exit_info\r","      elseif exit_uid == uid then\r","\r","        -- here if room leads back to itself\r","        exit_info = stub_exit_info\r","        linetype = miniwin.pen_dash -- dash\r","\r","      else\r","        if (not show_other_areas and rooms [exit_uid].area ~= current_area) or\r","           (not show_up_down and (dir == \"u\" or dir == \"d\")) then\r","            exit_info = stub_exit_info    -- don't show other areas\r","        else\r","          -- if we are scheduled to draw the room already, only draw a stub this time\r","          if plan_to_draw [exit_uid] and plan_to_draw [exit_uid] ~= next_coords then\r","            -- here if room already going to be drawn\r","            exit_info = stub_exit_info\r","            linetype = miniwin.pen_dash -- dash\r","          else\r","            -- remember to draw room next iteration\r","            local new_path = copytable.deep (path)\r","            table.insert (new_path, { dir = dir, uid = exit_uid })\r","            table.insert (rooms_to_be_drawn, add_another_room (exit_uid, new_path, next_x, next_y))\r","            drawn_coords [next_coords] = exit_uid\r","            plan_to_draw [exit_uid] = next_coords\r","\r","            -- if exit room known\r","            if not rooms [exit_uid].unknown then\r","              local exit_time = last_visited [exit_uid] or 0\r","              local this_time = last_visited [uid] or 0\r","              local now = os.time ()\r","              if exit_time \u003e (now - config.LAST_VISIT_TIME.time) and\r","                 this_time \u003e (now - config.LAST_VISIT_TIME.time) then\r","                 linewidth = 2\r","              end -- if\r","            end -- if\r","          end -- if\r","        end -- if\r","      end -- if drawn on this spot\r","\r","      WindowLine (win, x + exit_info.x1, y + exit_info.y1, x + exit_info.x2, y + exit_info.y2, exit_line_colour, linetype, linewidth)\r","\r","      -- one-way exit?\r","\r","      if not rooms [exit_uid].unknown then\r","        local dest = rooms [exit_uid]\r","        -- if inverse direction doesn't point back to us, this is one-way\r","        if dest.exits [inverse_direction [dir]] ~= uid then\r","\r","          -- turn points into string, relative to where the room is\r","          local points = string.format (\"%i,%i,%i,%i,%i,%i\",\r","              x + arrow [1],\r","              y + arrow [2],\r","              x + arrow [3],\r","              y + arrow [4],\r","              x + arrow [5],\r","              y + arrow [6])\r","\r","          -- draw arrow\r","          WindowPolygon(win, points,\r","                        exit_line_colour, miniwin.pen_solid, 1,\r","                        exit_line_colour, miniwin.brush_solid,\r","                        true, true)\r","\r","        end -- one way\r","\r","      end -- if we know of the room where it does\r","\r","    end -- if we know what to do with this direction\r","  end -- for each exit\r","\r","\r","  if room.unknown then\r","    WindowCircleOp (win, miniwin.circle_rectangle, left, top, right, bottom,\r","                    config.UNKNOWN_ROOM_COLOUR.colour, miniwin.pen_dot, 1,  --  dotted single pixel pen\r","                    -1, miniwin.brush_null)  -- opaque, no brush\r","  else\r","    WindowCircleOp (win, miniwin.circle_rectangle, left, top, right, bottom,\r","                    0, miniwin.pen_null, 0,  -- no pen\r","                    room.fillcolour, room.fillbrush)  -- brush\r","\r","    WindowCircleOp (win, miniwin.circle_rectangle, left, top, right, bottom,\r","                    room.bordercolour, room.borderpen, room.borderpenwidth,  -- pen\r","                    -1, miniwin.brush_null)  -- opaque, no brush\r","  end -- if\r","\r","\r","  -- show up and down in case we can't get a line in\r","\r","  if room.exits.u then  -- line at top\r","    WindowLine (win, left, top, left + ROOM_SIZE, top, config.EXIT_COLOUR_UP_DOWN.colour, miniwin.pen_solid, 1)\r","  end -- if\r","  if room.exits.d then  -- line at bottom\r","    WindowLine (win, left, bottom, left + ROOM_SIZE, bottom, config.EXIT_COLOUR_UP_DOWN.colour, miniwin.pen_solid, 1)\r","  end -- if\r","  if room.exits ['in'] then  -- line at right\r","    WindowLine (win, left + ROOM_SIZE, top, left + ROOM_SIZE, bottom, config.EXIT_COLOUR_IN_OUT.colour, miniwin.pen_solid, 1)\r","  end -- if\r","  if room.exits.out then  -- line at left\r","    WindowLine (win, left, top, left, bottom, config.EXIT_COLOUR_IN_OUT.colour, miniwin.pen_solid , 1)\r","  end -- if\r","\r","  speedwalks [uid] = path  -- so we know how to get here\r","\r","  WindowAddHotspot(win, uid,\r","                 left, top, right, bottom,       -- rectangle\r","                 \"mapper.mouseover_room\",        -- mouseover\r","                 \"mapper.cancelmouseover_room\",  -- cancelmouseover\r","                 \"\",  -- mousedown\r","                 \"\",  -- cancelmousedown\r","                 \"mapper.mouseup_room\",  -- mouseup\r","                 room.hovermessage,\r","                 miniwin.cursor_hand, 0)  -- hand cursor\r","\r","  WindowScrollwheelHandler (win, uid, \"mapper.zoom_map\")\r","\r","end -- draw_room\r","\r","local function changed_room (uid)\r","\r","  hyperlink_paths = nil  -- those hyperlinks are meaningless now\r","  speedwalks = {}  -- old speedwalks are irrelevant\r","\r","  if current_speedwalk then\r","\r","    if uid ~= expected_room then\r","      local exp = rooms [expected_room]\r","      if not exp then\r","        exp = get_room (expected_room) or { name = expected_room }\r","      end -- if\r","      local here = rooms [uid]\r","      if not here then\r","        here = get_room (uid) or { name = uid }\r","      end -- if\r","      exp = expected_room\r","      here = uid\r","      maperror (string.format (\"Speedwalk failed! Expected to be in '%s' but ended up in '%s'.\", exp or \"\u003cnone\u003e\", here))\r","      cancel_speedwalk ()\r","    else\r","      if #current_speedwalk \u003e 0 then\r","        local dir = table.remove (current_speedwalk, 1)\r","        SetStatus (\"Walking \" .. (expand_direction [dir.dir] or dir.dir) ..\r","                   \" to \" .. walk_to_room_name ..\r","                   \". Speedwalks to go: \" .. #current_speedwalk + 1)\r","        expected_room = dir.uid\r","        if config.DELAY.time \u003e 0 then\r","          if GetOption (\"enable_timers\") ~= 1 then\r","            maperror (\"WARNING! Timers not enabled. Speedwalking will not work properly.\")\r","          end -- if timers disabled\r","          DoAfter (config.DELAY.time, dir.dir)\r","        else\r","          Send (dir.dir)\r","        end -- if\r","      else\r","        last_hyperlink_uid = nil\r","        last_speedwalk_uid = nil\r","        if show_completed then\r","          mapprint (\"Speedwalk completed.\")\r","        end -- if wanted\r","        cancel_speedwalk ()\r","      end -- if any left\r","    end -- if expected room or not\r","  end -- if have a current speedwalk\r","\r","end -- changed_room\r","\r","local function draw_zone_exit (exit)\r","\r","  local x, y = exit.x, exit.y\r","  local offset = ROOM_SIZE\r","\r","  -- draw circle around us\r","  WindowCircleOp (win, miniwin.circle_ellipse,\r","                  x - offset, y - offset,\r","                  x + offset, y + offset,\r","                  ColourNameToRGB \"cornflowerblue\",  -- pen colour\r","                  miniwin.pen_solid, -- solid pen\r","                  3, -- pen width\r","                  0, -- brush colour\r","                  miniwin.brush_null) -- null brush\r","\r","  WindowCircleOp (win, miniwin.circle_ellipse,\r","                  x - offset, y - offset,\r","                  x + offset, y + offset,\r","                  ColourNameToRGB \"cyan\",  -- pen colour\r","                  miniwin.pen_solid, -- solid pen\r","                  1, -- pen width\r","                  0, -- brush colour\r","                  miniwin.brush_null) -- null brush\r","\r","end --  draw_zone_exit\r","\r","\r","----------------------------------------------------------------------------------\r","--  EXPOSED FUNCTIONS\r","----------------------------------------------------------------------------------\r","\r","-- can we find another room right now?\r","\r","function check_we_can_find ()\r","\r","  if not check_connected () then\r","    return\r","  end -- if\r","\r"," if not current_room then\r","    mapprint (\"I don't know where you are right now - try: LOOK\")\r","    return false\r","  end -- if\r","\r","  if current_speedwalk then\r","    mapprint (\"No point doing this while you are speedwalking.\")\r","    return false\r","  end -- if\r","\r","  return true\r","end -- check_we_can_find\r","\r","-- see: http://www.gammon.com.au/forum/?id=7306\u0026page=2\r","-- Thanks to Ked.\r","\r","-- uid is starting room\r","-- f returns true (or a \"reason\" string) if we want to store this one, and true,true if\r","--   we have done searching (ie. all wanted rooms found)\r","\r","function find_paths (uid, f)\r","\r","  local function make_particle (curr_loc, prev_path)\r","    local prev_path = prev_path or {}\r","    return {current_room=curr_loc, path=prev_path}\r","  end\r","\r","  local depth = 0\r","  local count = 0\r","  local done = false\r","  local found, reason\r","  local explored_rooms, particles = {}, {}\r","\r","  -- this is where we collect found paths\r","  -- the table is keyed by destination, with paths as values\r","  local paths = {}\r","\r","  -- create particle for the initial room\r","  table.insert (particles, make_particle (uid))\r","\r","  while (not done) and #particles \u003e 0 and depth \u003c config.SCAN.depth do\r","\r","    -- create a new generation of particles\r","    new_generation = {}\r","    depth = depth + 1\r","\r","    SetStatus (string.format (\"Scanning: %i/%i depth (%i rooms)\", depth, config.SCAN.depth, count))\r","\r","    -- process each active particle\r","    for i, part in ipairs (particles) do\r","\r","      count = count + 1\r","\r","      if not rooms [part.current_room] then\r","        rooms [part.current_room] = get_room (part.current_room)\r","      end -- if not in memory yet\r","\r","      -- if room doesn't exist, forget it\r","      if rooms [part.current_room] then\r","\r","        -- get a list of exits from the current room\r","        exits = rooms [part.current_room].exits\r","\r","        -- create one new particle for each exit\r","        for dir, dest in pairs(exits) do\r","\r","          -- if we've been in this room before, drop it\r","          if not explored_rooms[dest] then\r","            explored_rooms[dest] = true\r","            rooms [dest] = supplied_get_room (dest)  -- make sure this room in table\r","            if rooms [dest] then\r","              new_path = copytable.deep (part.path)\r","            -- Check for custom exit\r","\t\t\tlocal custom_dir = nil\r","\t\t\tif supplied_get_custom_exit_for_direction then\r","\t\t\t  custom_dir = supplied_get_custom_exit_for_direction(part.current_room, dir, dest)\r","\t\t\tend\r","\r","\t\t\ttable.insert(new_path, { dir = custom_dir or dir, uid = dest })\r","\r","              -- if this room is in the list of destinations then save its path\r","              found, done = f (dest)\r","              if found then\r","                paths[dest] = { path = new_path, reason = found }\r","              end -- found one!\r","\r","              -- make a new particle in the new room\r","              table.insert(new_generation, make_particle(dest, new_path))\r","            end -- if room exists\r","          end -- not explored this room\r","          if done then\r","            break\r","          end\r","\r","        end  -- for each exit\r","\r","      end -- if room exists\r","\r","      if done then\r","        break\r","      end\r","    end  -- for each particle\r","\r","    particles = new_generation\r","  end   -- while more particles\r","\r","  SetStatus \"Ready\"\r","  return paths, count, depth\r","end -- function find_paths\r","\r","-- draw our map starting at room: uid\r","\r","function draw (uid)\r","\r","  if not uid then\r","    maperror \"Cannot draw map right now, I don't know where you are - try: LOOK\"\r","    return\r","  end -- if\r","\r","  if current_room and current_room ~= uid then\r","    changed_room (uid)\r","  end -- if\r","\r","  current_room = uid -- remember where we are\r","\r","  -- timing\r","  local start_time = utils.timer ()\r","\r","  -- start with initial room\r","  rooms = { [uid] = get_room (uid) }\r","\r","  -- lookup current room\r","  local room = rooms [uid]\r","\r","  room = room or { name = \"\u003cUnknown room\u003e\", area = \"\u003cUnknown area\u003e\" }\r","  last_visited [uid] = os.time ()\r","\r","  current_area = room.area\r","\r","  -- we are recreating the window so any mouse-over is not valid any more\r","  if WindowInfo (win, 19) and WindowInfo (win, 19) ~= \"\" then\r","    if type (room_cancelmouseover) == \"function\" then\r","      room_cancelmouseover (WindowInfo (win, 19), 0)  -- cancelled mouse over\r","    end -- if\r","  end -- have a hotspot\r","\r","  WindowDeleteAllHotspots (win)\r","\r","  WindowCreate (win,\r","                 windowinfo.window_left,\r","                 windowinfo.window_top,\r","                 config.WINDOW.width,\r","                 config.WINDOW.height,\r","                 windowinfo.window_mode,   -- top right\r","                 windowinfo.window_flags,\r","                 config.BACKGROUND_COLOUR.colour)\r","\r","  -- let them move it around\r","  movewindow.add_drag_handler (win, 0, 0, 0, font_height + 4)\r","\r","  -- for zooming\r","  WindowAddHotspot(win,\r","                   \"zzz_zoom\",\r","                   0, 0, 0, 0,\r","                   \"\", \"\", \"\", \"\", \"\",\r","                   \"\",  -- hint\r","                   miniwin.cursor_arrow, 0)\r","\r","  WindowScrollwheelHandler (win, \"zzz_zoom\", \"mapper.zoom_map\")\r","\r","  -- set up for initial room, in middle\r","  drawn, drawn_coords, rooms_to_be_drawn, speedwalks, plan_to_draw, area_exits = {}, {}, {}, {}, {}, {}\r","  depth = 0\r","\r","  -- insert initial room\r","  table.insert (rooms_to_be_drawn, add_another_room (uid, {}, config.WINDOW.width / 2, config.WINDOW.height / 2))\r","\r","  while #rooms_to_be_drawn \u003e 0 and depth \u003c config.SCAN.depth do\r","    local old_generation = rooms_to_be_drawn\r","    rooms_to_be_drawn = {}  -- new generation\r","    for i, part in ipairs (old_generation) do\r","      draw_room (part.uid, part.path, part.x, part.y)\r","    end -- for each existing room\r","    depth = depth + 1\r","  end -- while all rooms_to_be_drawn\r","\r","  for area, zone_exit in pairs (area_exits) do\r","    draw_zone_exit (zone_exit)\r","  end -- for\r","\r","  local room_name = room.name\r","  local name_width = WindowTextWidth (win, FONT_ID, room_name, true)\r","  local add_dots = false\r","\r","  -- truncate name if too long\r","  while name_width \u003e (config.WINDOW.width - 10) do\r","    -- get rid of last word\r","    local s = string.match (\" \" .. room_name .. \"...\", \"(%s%S*)$\")\r","    if not s or #s == 0 then break end\r","    room_name = room_name:sub (1, - (#s - 2))  -- except the last 3 dots but add the space\r","    name_width = WindowTextWidth (win, FONT_ID, room_name .. \" ...\", true)\r","    add_dots = true\r","  end -- while\r","\r","  if add_dots then\r","    room_name = room_name .. \" ...\"\r","  end -- if\r","\r","  -- room name\r","\r","  draw_text_box (win, FONT_ID,\r","                 (config.WINDOW.width - WindowTextWidth (win, FONT_ID, room_name, true)) / 2,   -- left\r","                 3,    -- top\r","                 room_name, true,             -- what to draw, utf8\r","                 config.ROOM_NAME_TEXT.colour,   -- text colour\r","                 config.ROOM_NAME_FILL.colour,   -- fill colour\r","                 config.ROOM_NAME_BORDER.colour)     -- border colour\r","\r","  -- area name\r","\r","  local areaname = room.area\r","\r","  if areaname then\r","    draw_text_box (win, FONT_ID,\r","                   (config.WINDOW.width - WindowTextWidth (win, FONT_ID, areaname, true)) / 2,   -- left\r","                   config.WINDOW.height - 6 - font_height,    -- top\r","                   areaname, true,              -- what to draw, utf8\r","                   config.AREA_NAME_TEXT.colour,   -- text colour\r","                   config.AREA_NAME_FILL.colour,   -- fill colour\r","                   config.AREA_NAME_BORDER.colour)     -- border colour\r","  end -- if area known\r","\r","  -- configure?\r","\r","  if draw_configure_box then\r","    draw_configuration ()\r","  else\r","\r","    local x = 5\r","    local y = config.WINDOW.height - 6 - font_height\r","    local width = draw_text_box (win, FONT_ID,\r","                   x,   -- left\r","                   y,   -- top (ie. at bottom)\r","                   \"*\", true,                   -- what to draw, utf8\r","                   config.AREA_NAME_TEXT.colour,   -- text colour\r","                   config.AREA_NAME_FILL.colour,   -- fill colour\r","                   config.AREA_NAME_BORDER.colour)     -- border colour\r","\r","    WindowAddHotspot(win, \"\u003cconfigure\u003e\",\r","                   x, y, x + width, y + font_height,   -- rectangle\r","                   \"\",  -- mouseover\r","                   \"\",  -- cancelmouseover\r","                   \"\",  -- mousedown\r","                   \"\",  -- cancelmousedown\r","                   \"mapper.mouseup_configure\",  -- mouseup\r","                   \"Click to configure map\",\r","                   miniwin.cursor_hand, 0)  -- hand cursor\r","  end -- if\r","\r","  if type (show_help) == \"function\" then\r","    local x = config.WINDOW.width - WindowTextWidth (win, FONT_ID, \"?\", true) - 5\r","    local y = config.WINDOW.height - 6 - font_height\r","    local width = draw_text_box (win, FONT_ID,\r","                   x,   -- left\r","                   y,   -- top (ie. at bottom)\r","                   \"?\", true,                   -- what to draw, utf8\r","                   config.AREA_NAME_TEXT.colour,   -- text colour\r","                   config.AREA_NAME_FILL.colour,   -- fill colour\r","                   config.AREA_NAME_BORDER.colour)     -- border colour\r","\r","    WindowAddHotspot(win, \"\u003chelp\u003e\",\r","                   x, y, x + width, y + font_height,   -- rectangle\r","                   \"\",  -- mouseover\r","                   \"\",  -- cancelmouseover\r","                   \"\",  -- mousedown\r","                   \"\",  -- cancelmousedown\r","                   \"mapper.show_help\",  -- mouseup\r","                   \"Click for help\",\r","                   miniwin.cursor_hand, 0)  -- hand cursor\r","  end -- if\r","\r","  -- 3D box around whole thing\r","\r","  draw_3d_box (win, 0, 0, config.WINDOW.width, config.WINDOW.height)\r","\r","  -- make sure window visible\r","  WindowShow (win, not hidden)\r","\r","  last_drawn = uid  -- last room number we drew (for zooming)\r","\r","  local end_time = utils.timer ()\r","\r","  -- timing stuff\r","  if timing then\r","    local count= 0\r","    for k in pairs (drawn) do\r","      count = count + 1\r","    end\r","    print (string.format (\"Time to draw %i rooms = %0.3f seconds, search depth = %i\", count, end_time - start_time, depth))\r","\r","    total_times_drawn = total_times_drawn + 1\r","    total_time_taken = total_time_taken + end_time - start_time\r","\r","    print (string.format (\"Total times map drawn = %i, average time to draw = %0.3f seconds\",\r","                          total_times_drawn,\r","                          total_time_taken / total_times_drawn))\r","  end -- if\r","\r","end -- draw\r","\r","local credits = {\r","  \"MUSHclient mapper\",\r","  string.format (\"Version %0.1f\", VERSION),\r","  \"Written by Nick Gammon\",\r","  WorldName (),\r","  GetInfo (3),\r","  }\r","\r","-- call once to initialize the mapper\r","function init (t)\r","\r","  -- make copy of colours, sizes etc.\r","  config = t.config\r","  assert (type (config) == \"table\", \"No 'config' table supplied to mapper.\")\r","\r","  supplied_get_room = t.get_room\r","  assert (type (supplied_get_room) == \"function\", \"No 'get_room' function supplied to mapper.\")\r","\r","  show_help = t.show_help     -- \"help\" function\r","  room_click = t.room_click   -- RH mouse-click function\r","  room_mouseover = t.room_mouseover -- mouse-over function\r","  room_cancelmouseover = t.room_cancelmouseover -- cancel mouse-over function\r","  timing = t.timing           -- true for timing info\r","  show_completed = t.show_completed  -- true to show \"Speedwalk completed.\" message\r","  show_other_areas = t.show_other_areas  -- true to show other areas\r","  show_up_down = t.show_up_down        -- true to show up or down\r","  show_area_exits = t.show_area_exits  -- true to show area exits\r","  speedwalk_prefix = t.speedwalk_prefix  -- how to speedwalk (prefix)\r","  supplied_get_custom_exit_for_direction = t.get_custom_exit_for_direction or rawget(_G, 'get_custom_exit_for_direction')\r","\r","  -- force some config defaults if not supplied\r","  for k, v in pairs (default_config) do\r","    config [k] = config [k] or v\r","  end -- for\r","\r","  win = GetPluginID () .. \"_mapper\"\r","\r","  WindowCreate (win, 0, 0, 0, 0, 0, 0, 0)\r","\r","  -- add the fonts\r","  WindowFont (win, FONT_ID, config.FONT.name, config.FONT.size)\r","  WindowFont (win, FONT_ID_UL, config.FONT.name, config.FONT.size, false, false, true)\r","\r","  -- see how high it is\r","  font_height = WindowFontInfo (win, FONT_ID, 1)  -- height\r","\r","  -- find where window was last time\r","  windowinfo = movewindow.install (win, miniwin.pos_center_right)\r","\r","  -- calculate box sizes, arrows, connecting lines etc.\r","  build_room_info ()\r","\r","  WindowCreate (win,\r","                 windowinfo.window_left,\r","                 windowinfo.window_top,\r","                 config.WINDOW.width,\r","                 config.WINDOW.height,\r","                 windowinfo.window_mode,   -- top right\r","                 windowinfo.window_flags,\r","                 config.BACKGROUND_COLOUR.colour)\r","\r","  -- let them move it around\r","  movewindow.add_drag_handler (win, 0, 0, 0, font_height + 4)\r","\r","  local top = (config.WINDOW.height - #credits * font_height) /2\r","\r","  for _, v in ipairs (credits) do\r","    local width = WindowTextWidth (win, FONT_ID, v, true)\r","    local left = (config.WINDOW.width - width) / 2\r","    WindowText   (win, FONT_ID, v, left, top, 0, 0, config.ROOM_COLOUR.colour, true)\r","    top = top + font_height\r","  end -- for\r","\r","  draw_3d_box (win, 0, 0, config.WINDOW.width, config.WINDOW.height)\r","\r","  WindowShow (win, true)\r","\r","end -- init\r","\r","function zoom_in ()\r","  if last_drawn and ROOM_SIZE \u003c 40 then\r","    ROOM_SIZE = ROOM_SIZE + 2\r","    DISTANCE_TO_NEXT_ROOM = DISTANCE_TO_NEXT_ROOM + 2\r","    build_room_info ()\r","    draw (last_drawn)\r","  end -- if\r","end -- zoom_in\r","\r","\r","function zoom_out ()\r","  if last_drawn and ROOM_SIZE \u003e 4 then\r","    ROOM_SIZE = ROOM_SIZE - 2\r","    DISTANCE_TO_NEXT_ROOM = DISTANCE_TO_NEXT_ROOM - 2\r","    build_room_info ()\r","    draw (last_drawn)\r","  end -- if\r","end -- zoom_out\r","\r","function mapprint (...)\r","  local old_note_colour = GetNoteColourFore ()\r","  SetNoteColourFore(config.MAPPER_NOTE_COLOUR.colour)\r","  print (...)\r","  SetNoteColourFore (old_note_colour)\r","end -- mapprint\r","\r","function maperror (...)\r","  local old_note_colour = GetNoteColourFore ()\r","  SetNoteColourFore(ColourNameToRGB \"red\")\r","  print (...)\r","  SetNoteColourFore (old_note_colour)\r","end -- maperror\r","\r","function show ()\r","  WindowShow (win, true)\r","  hidden = false\r","end -- show\r","\r","function hide ()\r","  WindowShow (win, false)\r","  hidden = true\r","end -- hide\r","\r","function save_state ()\r","  movewindow.save_state (win)\r","end -- save_state\r","\r","\r","-- generic room finder\r","\r","-- f (uid) is a function which returns: found, done\r","--    found is not nil if uid is a wanted room - if it is a string it is the reason it matched (eg. shop)\r","--    done is true if we know there is nothing else to search for (eg. all rooms found)\r","\r","-- show_uid is true if you want the room uid to be displayed\r","\r","-- expected_count is the number we expect to find (eg. the number found on a database)\r","\r","-- if 'walk' is true, we walk to the first match rather than displaying hyperlinks\r","\r","-- if fcb is a function, it is called back after displaying each line\r","\r","function find (f, show_uid, expected_count, walk, fcb)\r","\r","  if not check_we_can_find () then\r","    return\r","  end -- if\r","\r","  if fcb then\r","    assert (type (fcb) == \"function\")\r","  end -- if\r","\r","  local start_time = utils.timer ()\r","  local paths, count, depth = find_paths (current_room, f)\r","  local end_time = utils.timer ()\r","\r","  local t = {}\r","  local found_count = 0\r","  for k in pairs (paths) do\r","    table.insert (t, k)\r","    found_count = found_count + 1\r","  end -- for\r","\r","  -- timing stuff\r","  if timing then\r","    print (string.format (\"Time to search %i rooms = %0.3f seconds, search depth = %i\",\r","                          count, end_time - start_time, depth))\r","  end -- if\r","\r","  if found_count == 0 then\r","    mapprint (\"No matches.\")\r","    return\r","  end -- if\r","\r","  if found_count == 1 and walk then\r","    uid, item = next (paths, nil)\r","    mapprint (\"Walking to:\", rooms [uid].name)\r","    start_speedwalk (item.path)\r","    return\r","  end -- if walking wanted\r","\r","  -- sort so closest ones are first\r","  table.sort (t, function (a, b) return #paths [a].path \u003c #paths [b].path end )\r","\r","  hyperlink_paths = {}\r","\r","  for _, uid in ipairs (t) do\r","    local room = rooms [uid] -- ought to exist or wouldn't be in table\r","\r","    assert (room, \"Room \" .. uid .. \" is not in rooms table.\")\r","\r","    if current_room == uid then\r","      mapprint (room.name, \"is the room you are in\")\r","    else\r","      local distance = #paths [uid].path .. \" room\"\r","      if #paths [uid].path \u003e 1 then\r","        distance = distance .. \"s\"\r","      end -- if\r","      distance = distance .. \" away\"\r","\r","      local room_name = room.name\r","      if show_uid then\r","        room_name = room_name .. \" (\" .. uid .. \")\"\r","      end -- if\r","\r","      -- in case the same UID shows up later, it is only valid from the same room\r","      local hash = utils.tohex (utils.md5 (tostring (current_room) .. \"\u003c--\u003e\" .. tostring (uid)))\r","\r","      Hyperlink (\"!!\" .. GetPluginID () .. \":mapper.do_hyperlink(\" .. hash .. \")\",\r","                room_name, \"Click to speedwalk there (\" .. distance .. \")\", \"\", \"\", false)\r","      local info = \"\"\r","      if type (paths [uid].reason) == \"string\" and paths [uid].reason ~= \"\" then\r","        info = \" [\" .. paths [uid].reason .. \"]\"\r","      end -- if\r","      mapprint (\" - \" .. distance .. info) -- new line\r","\r","      -- callback to display extra stuff (like find context, room description)\r","      if fcb then\r","        fcb (uid)\r","      end -- if callback\r","      hyperlink_paths [hash] = paths [uid].path\r","    end -- if\r","  end -- for each room\r","\r","  if expected_count and found_count \u003c expected_count then\r","    local diff = expected_count - found_count\r","    local were, matches = \"were\", \"matches\"\r","    if diff == 1 then\r","      were, matches = \"was\", \"match\"\r","    end -- if\r","    mapprint (\"There\", were, diff, matches,\r","              \"which I could not find a path to within\",\r","              config.SCAN.depth, \"rooms.\")\r","  end -- if\r","\r","end -- map_find_things\r","\r","-- executed when the mapper draws a hyperlink to a room\r","\r","function do_hyperlink (hash)\r","\r","  if not check_connected () then\r","    return\r","  end -- if\r","\r","  if not hyperlink_paths or not hyperlink_paths [hash] then\r","    mapprint (\"Hyperlink is no longer valid, as you have moved.\")\r","    return\r","  end -- if\r","\r","  local path = hyperlink_paths [hash]\r","  if #path \u003e 0 then\r","    last_hyperlink_uid = path [#path].uid\r","  end -- if\r","  start_speedwalk (path)\r","\r","end -- do_hyperlink\r","\r","-- build a speedwalk from a path into a string\r","\r","function build_speedwalk (path)\r","\r"," -- build speedwalk string (collect identical directions)\r","  local tspeed = {}\r","  for _, dir in ipairs (path) do\r","    local n = #tspeed\r","    if n == 0 then\r","      table.insert (tspeed, { dir = dir.dir, count = 1 })\r","    else\r","      if tspeed [n].dir == dir.dir then\r","        tspeed [n].count = tspeed [n].count + 1\r","      else\r","        table.insert (tspeed, { dir = dir.dir, count = 1 })\r","      end -- if different direction\r","    end -- if\r","  end -- for\r","\r","  if #tspeed == 0 then\r","    return\r","  end -- nowhere to go (current room?)\r","\r","  -- now build string like: 2n3e4(sw)\r","  local s = \"\"\r","\r","  for _, dir in ipairs (tspeed) do\r","    if dir.count \u003e 1 then\r","      s = s .. dir.count\r","    end -- if\r","    if #dir.dir == 1 then\r","      s = s .. dir.dir\r","    else\r","      s = s .. \"(\" .. dir.dir .. \")\"\r","    end -- if\r","    s = s .. \" \"\r","  end -- if\r","\r","  return s\r","\r","end -- build_speedwalk\r","\r","-- start a speedwalk to a path\r","\r","function start_speedwalk (path)\r","\r","  if not check_connected () then\r","    return\r","  end -- if\r","\r","  if current_speedwalk and #current_speedwalk \u003e 0 then\r","    mapprint (\"You are already speedwalking! (Ctrl + LH-click on any room to cancel)\")\r","    return\r","  end -- if\r","\r","  current_speedwalk = path\r","\r","  if current_speedwalk then\r","    if #current_speedwalk \u003e 0 then\r","      last_speedwalk_uid = current_speedwalk [#current_speedwalk].uid\r","\r","      -- fast speedwalk: just send # 4s 3e  etc.\r","      if type (speedwalk_prefix) == \"string\" and speedwalk_prefix ~= \"\" then\r","        local s = speedwalk_prefix .. \" \" .. build_speedwalk (path)\r","        Execute (s)\r","        current_speedwalk = nil\r","        return\r","      end -- if\r","\r","      local dir = table.remove (current_speedwalk, 1)\r","      local room = get_room (dir.uid)\r","      walk_to_room_name = room.name\r","      SetStatus (\"Walking \" .. (expand_direction [dir.dir] or dir.dir) ..\r","                 \" to \" .. walk_to_room_name ..\r","                 \". Speedwalks to go: \" .. #current_speedwalk + 1)\r","      Send (dir.dir)\r","      expected_room = dir.uid\r","    else\r","      cancel_speedwalk ()\r","    end -- if any left\r","  end -- if\r","\r","end -- start_speedwalk\r","\r","-- cancel the current speedwalk\r","\r","function cancel_speedwalk ()\r","  if current_speedwalk and #current_speedwalk \u003e 0 then\r","    mapprint \"Speedwalk cancelled.\"\r","  end -- if\r","  current_speedwalk = nil\r","  expected_room = nil\r","  hyperlink_paths = nil\r","  SetStatus (\"Ready\")\r","end -- cancel_speedwalk\r","\r","\r","-- ------------------------------------------------------------------\r","-- mouse-up handlers (need to be exposed)\r","-- these are for clicking on the map, or the configuration box\r","-- ------------------------------------------------------------------\r","\r","function mouseup_room (flags, hotspot_id)\r","  local uid = hotspot_id\r","\r","  if bit.band (flags, miniwin.hotspot_got_rh_mouse) ~= 0 then\r","\r","    -- RH click\r","\r","    if type (room_click) == \"function\" then\r","      room_click (uid, flags)\r","    end -- if\r","\r","    return\r","  end -- if RH click\r","\r","  -- here for LH click\r","\r","   -- Control key down?\r","  if bit.band (flags, miniwin.hotspot_got_control) ~= 0 then\r","    cancel_speedwalk ()\r","    return\r","  end -- if ctrl-LH click\r","\r","  start_speedwalk (speedwalks [uid])\r","\r","end -- mouseup_room\r","\r","-- ------------------------------------------------------------------\r","-- mouse-over handlers (need to be exposed)\r","-- these are for mousing over a room\r","-- ------------------------------------------------------------------\r","\r","function mouseover_room (flags, hotspot_id)\r","  if type (room_mouseover) == \"function\" then\r","    room_mouseover (hotspot_id, flags)  -- moused over\r","  end -- if\r","end -- mouseover_room\r","\r","function cancelmouseover_room (flags, hotspot_id)\r","  if type (room_cancelmouseover) == \"function\" then\r","    room_cancelmouseover (hotspot_id, flags)  -- cancled mouse over\r","  end -- if\r","end -- cancelmouseover_room\r","\r","function mouseup_configure (flags, hotspot_id)\r","  draw_configure_box = true\r","  draw (current_room)\r","end -- mouseup_configure\r","\r","function mouseup_close_configure (flags, hotspot_id)\r","  draw_configure_box = false\r","  draw (current_room)\r","end -- mouseup_player\r","\r","function mouseup_change_colour (flags, hotspot_id)\r","\r","  local which = string.match (hotspot_id, \"^$colour:([%a%d_]+)$\")\r","  if not which then\r","    return  -- strange ...\r","  end -- not found\r","\r","  local newcolour = PickColour (config [which].colour)\r","\r","  if newcolour == -1 then\r","    return\r","  end -- if dismissed\r","\r","  config [which].colour = newcolour\r","\r","  draw (current_room)\r","end -- mouseup_change_colour\r","\r","function mouseup_change_font (flags, hotspot_id)\r","\r","  local newfont =  utils.fontpicker (config.FONT.name, config.FONT.size, config.ROOM_NAME_TEXT.colour)\r","\r","  if not newfont then\r","    return\r","  end -- if dismissed\r","\r","  config.FONT.name = newfont.name\r","\r","  if newfont.size \u003e 12 then\r","    utils.msgbox (\"Maximum allowed font size is 12 points.\", \"Font too large\", \"ok\", \"!\", 1)\r","  else\r","    config.FONT.size = newfont.size\r","  end -- if\r","\r","  config.ROOM_NAME_TEXT.colour = newfont.colour\r","\r","  -- reload new font\r","  WindowFont (win, FONT_ID, config.FONT.name, config.FONT.size)\r","  WindowFont (win, FONT_ID_UL, config.FONT.name, config.FONT.size, false, false, true)\r","\r","  -- see how high it is\r","  font_height = WindowFontInfo (win, FONT_ID, 1)  -- height\r","\r","  draw (current_room)\r","end -- mouseup_change_font\r","\r","\r","function mouseup_change_width (flags, hotspot_id)\r","\r","  local width = get_number_from_user (\"Choose window width (200 to 1000 pixels)\", \"Width\", config.WINDOW.width, 200, 1000)\r","\r","  if not width then\r","    return\r","  end -- if dismissed\r","\r","  config.WINDOW.width = width\r","  draw (current_room)\r","end -- mouseup_change_width\r","\r","function mouseup_change_height (flags, hotspot_id)\r","\r","  local height = get_number_from_user (\"Choose window height (200 to 1000 pixels)\", \"Width\", config.WINDOW.height, 200, 1000)\r","\r","  if not height then\r","    return\r","  end -- if dismissed\r","\r","  config.WINDOW.height = height\r","  draw (current_room)\r","end -- mouseup_change_height\r","\r","function mouseup_change_depth (flags, hotspot_id)\r","\r","  local depth = get_number_from_user (\"Choose scan depth (3 to 100 rooms)\", \"Depth\", config.SCAN.depth, 3, 100)\r","\r","  if not depth then\r","    return\r","  end -- if dismissed\r","\r","  config.SCAN.depth = depth\r","  draw (current_room)\r","end -- mouseup_change_depth\r","\r","function mouseup_change_delay (flags, hotspot_id)\r","\r","  local delay = get_number_from_user (\"Choose speedwalk delay time (0 to 10 seconds)\", \"Delay in seconds\", config.DELAY.time, 0, 10, true)\r","\r","  if not delay then\r","    return\r","  end -- if dismissed\r","\r","  config.DELAY.time = delay\r","  draw (current_room)\r","end -- mouseup_change_delay\r","\r","function zoom_map (flags, hotspot_id)\r","\r","  if bit.band (flags, 0x100) ~= 0 then\r","    zoom_out ()\r","  else\r","    zoom_in ()\r","  end -- if\r","end -- zoom_map\r","\r","\r"],"stylingDirectives":null,"colorizedLines":["\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e--\u003c/span\u003e mapper.lua\u003c/span\u003e","","\u003cspan class=\"pl-c\"\u003e\u003cspan class=\"pl-c\"\u003e--[[\u003c/span\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eAuthor: Nick Gammon\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eDate:   11th March 2010\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eAmended: 15th August 2010\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eAmended: 2nd October 2010\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eAmended: 18th October 2010 to added find callback\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eAmended: 16th November 2010 to add symbolic constants (miniwin.xxxx)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eAmended: 18th November 2010 to add more timing and count of times called\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e         Also added zooming with the mouse wheel.\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eAmended: 26th November 2010 to check timers are enabled when speedwalking.\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eAmended: 11th November 2014 to allow for detecting mouse-overs of rooms\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eGeneric MUD mapper.\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eExposed functions:\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003einit (t)            -- call once, supply:\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.config      -- ie. colours, sizes\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.get_room    -- info about room (uid)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.show_help   -- function that displays some help\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.room_click  -- function that handles RH click on room (uid, flags)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.room_mouseover -- function that handles mouse-over a room (uid, flags)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.room_cancelmouseover -- function that handles cancelled mouse-over of a room (uid, flags)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.timing      -- true to show timing\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.show_completed  -- true to show \u0026quot;Speedwalk completed.\u0026quot;\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.show_other_areas -- true to show non-current areas\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.show_up_down    -- follow up/down exits\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.show_area_exits  -- true to draw a circle around rooms leading to other areas\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e                          t.speedwalk_prefix   -- if not nil, speedwalk by prefixing with this\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003ezoom_in ()          -- zoom in map view\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003ezoom_out ()         -- zoom out map view\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003emapprint (message)  -- like print, but uses mapper colour\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003emaperror (message)  -- like print, but prints in red\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003ehide ()             -- hides map window (eg. if plugin disabled)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eshow ()             -- show map window  (eg. if plugin enabled)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003esave_state ()       -- call to save plugin state (ie. in OnPluginSaveState)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003edraw (uid)          -- draw map - starting at room \u0026#39;uid\u0026#39;\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003estart_speedwalk (path)  -- starts speedwalking. path is a table of directions/uids\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003ebuild_speedwalk (path)  -- builds a client speedwalk string from path\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003ecancel_speedwalk ()     -- cancel current speedwalk, if any\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003echeck_we_can_find ()    -- returns true if doing a find is OK right now\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003efind (f, show_uid, count, walk)      -- generic room finder\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003efind_paths (uid, f)     -- lower-level room finder (for getting back a path)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eExposed variables:\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003ewin                 -- the window (in case you want to put up menus)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eVERSION             -- mapper version\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003elast_hyperlink_uid  -- room uid of last hyperlink click (destination)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003elast_speedwalk_uid  -- room uid of last speedwalk attempted (destination)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u0026lt;various functions\u0026gt; -- functions required to be global by the client (eg. for mouseup)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003eRoom info should include:\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e  name          (what to show as room name)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e  exits         (table keyed by direction, value is exit uid)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e  area          (area name)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e  hovermessage  (what to show when you mouse-over the room)\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e  bordercolour  (colour of room border)     - RGB colour\u003c/span\u003e","\u003cspan class=\"pl-c\"\u003e  borderpen     (pen style of room border)  